<template>
    <div class="fillcontain">
        <div class="table_container">
          <p>贸易金融平台--用户注册</p>
            <el-form :model="ruleForm" :rules="rules" ref="ruleForm" class="demo-ruleForm">
                <el-form-item label="用户名" prop="uesr">
                    <el-input type="text" v-model="ruleForm.user" auto-complete="off"></el-input>
                </el-form-item>
                <el-form-item label="密码" prop="pass">
                    <el-input type="password" v-model="ruleForm.pass" auto-complete="off"></el-input>
                </el-form-item>
                <el-form-item label="确认密码" prop="checkPass">
                    <el-input type="password" v-model="ruleForm.checkPass" auto-complete="off"></el-input>
                </el-form-item>
                <el-form-item prop="type">
                    <el-checkbox v-model="ruleForm.checked" label="我已认真阅读并同意"></el-checkbox><a href="#">《用户注册协议》</a>
                </el-form-item>
                <el-form-item>
                  <!-- <div id="slider">
                    <div class="drag_bg"></div>
                    <div class="drag_text" onselectstart="return false;" unselectable="on">拖动滑块验证</div>
                    <div class="handler handler_bg"></div>
                  </div> -->
                  <loginSider></loginSider>
                </el-form-item>
                <el-form-item>
                    <el-button @click="resetForm('ruleForm')">上一步</el-button>
                    <el-button type="primary" @click="submitForm('ruleForm')">提交</el-button>
                </el-form-item>
            </el-form>
        </div>
    </div>
</template>

<script>
    import loginSider from '../components/loginSider'
export default {
  data() {
    var validatePass = (rule, value, callback) => {
      if (value === "") {
        callback(new Error("请输入密码"));
      } else {
        if (this.ruleForm.checkPass !== "") {
          this.$refs.ruleForm.validateField("checkPass");
        }
        callback();
      }
    };
    var validatePass1 = (rule, value, callback) => {
      if (this.ruleForm.user) {
        callback();
      } else {
        callback(new Error("请输入正确的用户名"));
      }
    };
    var validatePass2 = (rule, value, callback) => {
      if (value === "") {
        callback(new Error("请再次输入密码"));
      } else if (value !== this.ruleForm.pass) {
        callback(new Error("两次输入密码不一致!"));
      } else {
        callback();
      }
    };
    var validatePass3 = (rule, value, callback) => {
      if (this.ruleForm.checked) {
        callback();
      } else {
        callback(new Error("必须勾选!"));
      }
    };
    return {
      value1: "",
      ruleForm: {
        pass: "",
        checkPass: "",
        user: "",
        checked: ""
      },
      rules: {
        pass: [{ validator: validatePass, trigger: "blur" }],
        uesr: [{ validator: validatePass1, trigger: "blur" }],
        checkPass: [{ validator: validatePass2, trigger: "blur" }],
        type: [{ validator: validatePass3, trigger: "change" }]
      }
    };
  },
  components: {
    loginSider
  },
  created() {},
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          alert("submit!");
          this.$router.push('signFlowFirmApprove')
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    },
    resetForm() {
      this.$router.push("/");
    }
  }
};
</script>

